package com.example.recycleview;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.View;
import android.widget.Toast;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity implements my_adapter.ItemClickListener{
    private RecyclerView rvMakananFav;
    private my_adapter adapter;
    List<modelmakananfaforit> listModelmakananfaforitMakanan;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        rvMakananFav = (RecyclerView)findViewById(R.id.rvMakananFav);
        rvMakananFav.setHasFixedSize(true);
        rvMakananFav.setLayoutManager(new LinearLayoutManager(this));

        adapter = new my_adapter(getList(), this);
        adapter.setClickListener(this);
        rvMakananFav.setAdapter(adapter);
    }

    private List<modelmakananfaforit> getList() {
        List<modelmakananfaforit> myModels = new ArrayList<>();

        myModels.add(new modelmakananfaforit("Molen", "data:image/jpeg;base64,","Molen pisang adalah camilan tradisional Indonesia yang terbuat dari pisang dan adonan pastry. Rasanya manis dan gurih, dengan tekstur renyah di luar dan lembut di dalam."));
        myModels.add(new modelmakananfaforit("Ayam goreng", "data:image/jpeg;base64,","Ayam goreng adalah hidangan yang terbuat dari potongan ayam yang digoreng dengan minyak panas. Hidangan ini populer di seluruh dunia dan memiliki banyak variasi dalam hal bumbu, metode penggorengan, dan penyajian."));
        myModels.add(new modelmakananfaforit("Ayam bakar", "data:image/jpeg;base64,","Ayam bakar adalah hidangan yang terbuat dari potongan ayam yang dibakar di atas api. Hidangan ini populer di seluruh dunia dan memiliki banyak variasi dalam hal bumbu, metode pembakaran, dan penyajian."));
        myModels.add(new modelmakananfaforit("Nasi goreng", "data:image/jpeg;base64,","Nasi goreng adalah hidangan nasi yang digoreng dengan berbagai bumbu dan bahan lainnya. Hidangan ini populer di Indonesia dan banyak negara di Asia Tenggara lainnya."));
        myModels.add(new modelmakananfaforit("Bubur ayam", "data:image/jpeg;base64,","Bubur ayam adalah hidangan bubur nasi yang disajikan dengan suwiran ayam, kuah kuning, dan berbagai pelengkap lainnya. Hidangan ini populer di Indonesia dan banyak negara di Asia Tenggara lainnya."));

        return myModels;
    }

    @Override
    public void onItemClick(View view, int position) {
        Toast.makeText(this, "You clicked " + adapter.getItem(position).getIsi() + " on row number " + position, Toast.LENGTH_SHORT).show();
    }
}